class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<pair<int,int>> v;
        int start=-1,end=-1,n=colors.size(),ans=0;
        if(colors[0]==colors[1]) v.push_back({0,0});
        for(int i=1;i<n;i++) {
            if(colors[i]!=colors[i-1])
            {
                if(start==-1)
                {
                    start=i-1;
                    end=i;
                }
                else
                {
                    end++;
                }
            }
            else
            {
                if(start!=-1)
                {
                    v.push_back({start,end});
                    start=-1,end=-1;
                }
            }
        }
        if(start!=-1)
        {
            v.push_back({start,end});
            start=-1,end=-1;
        }
        if(colors[n-1]==colors[n-2]) v.push_back({n-1,n-1});
        if(colors[0]!=colors[n-1])
        {
            if(!v.empty()&&v[0].first==0&&v.back().second==n-1&&v.size()!=1) {
                v[0].first-=(v.back().second-v.back().first+1);
                v.pop_back();
            }
        }
        if(v.size()==1&&v[0]==make_pair(0,n-1)&&colors[0]!=colors[n-1])
        {
            if(n>=k) return n; else return 0;
        }
        for(auto [start,end]:v)
        {
            cout<<start<<' '<<end<<endl;
            if(end-start+1>=k)
            {
                ans+=end-start+2-k;                
            }
        }
        return ans;
    }
};
