class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(),colors.begin(),colors.begin()+k-1);
        int a=0;
        int n=1;;
        for(size_t i=1;i<colors.size();i++){
            n=(colors[i]!=colors[i-1])?n+1:1;
            if(n>=k) ++a;
        }
        return a;

    }
};
