class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& cls, int k) {
        int n = cls.size();
        vector<int>pref(n, 0), suff(n, 0);
        if(cls[0]!=cls[n-1])pref[0] = 1;
        for(int i=1; i<n; i++){
            if(cls[i]!=cls[i-1])pref[i] = 1;
            pref[i]+=pref[i-1];
        }

        if(cls[n-1]!=cls[0])suff[n-1]=1;
        for(int i=n-2; i>=0; i--){
            if(cls[i+1]!=cls[i])suff[i] = 1;
        }
        for(int i=1; i<n; i++){
            suff[i]+= suff[i-1] ;
        }
        int ans = 0;
        for(int i=0; i<n; i++){
            if(i+k>n){
                int rem = (i+k)%n-1;
                int sum1 =0, sum2=0;
                sum1 = pref[n-1]-pref[i] + pref[rem];
                if(rem==0)sum2= suff[n-1]-suff[i] ;
                else  sum2 = suff[n-1]-suff[i] + suff[rem-1];
                if(sum1>=k-1 || sum2>=k-1)ans++;
                // cout<<sum1<<" "<<sum2<<" ";
            }else{
                if(pref[i]+k-1==pref[i+k-1] || suff[i]-k+1==suff[i+k-1])ans++;
            }
        }
        return ans;

    }
};
