class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<array<int,2>> lol;
        int prev=0, n=c.size();
        for(int i=1;i<n+k-1;i++)
        {
            if(c[(i-1)%n]==c[i%n])
            {
                lol.push_back({prev,i-1});
                prev=i;
            }
        }
        lol.push_back({prev,n+k-2});
        int ans=0;
        for(auto x:lol)
        {
            // cout<<x[0]<<" "<<x[1]<<endl;
            ans+=max(0,x[1]-x[0]+1-k+1);
        }
        return ans;
    }
};
