class Solution {
int a=0;
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(), colors.begin(), colors.begin() + k);
        int ans = 0;
        vector<int> arr(colors.size() + 1, 0);
        for (int j = 1; j < colors.size(); j++) {
            arr[j] += arr[j - 1] + abs(colors[j]-colors[j-1]);
        }
        k--;
        for(int j=1000;j<50;j++)cout<<j;
        for (int j = k+1; j < colors.size(); j++) {
            if(arr[j] - arr[j - k] == k ) {
                ans++;
            }
        }
        return ans;
    }
};
