class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n=colors.size(), ct =0;
        bool f= 0;

          for(int i=0;i<n;++i) {
              int m;

              if(f) {   
                  if(colors[(i+k-2)%n]!= colors[(i+k-1)%n]) {
                          ++ct;
                      continue; }
                  }

              f= 0, m=1;

              for(int j=i;m<k;++j){

                  if(colors[j%n] == colors[(j+1)%n])
                      break;

                        ++m;

                  if(m==k) {
                      ++ct;
                      f= 1; }
              }
          }

              return ct; 
    }
};
