func numberOfAlternatingGroups(colors []int, k int) (ans int) {
	xor := make([]int, len(colors)*2)
	double := append(colors, colors...)
	for i := range xor {
		xor[i] = (i % 2) ^ double[i]
	}

	sum := makePrefixSum(xor)
    //fmt.Println(xor, sum)
	for i := k; i-k < len(colors); i++ {
		if (i-k)%2 == colors[i-k] {
			if sum[i]-sum[i-k] == 0 {
				ans++
			}
		} else {
			if sum[i]-sum[i-k] == k {
				ans++
			}
		}
	}
	return
}

// 做前缀和
func makePrefixSum(nums []int) []int {
	sum := make([]int, len(nums)+1)
	for i := range nums {
		sum[i+1] = sum[i] + nums[i]
	}
	return sum
}
