class Solution:
    def numberOfAlternatingGroups(self, c: List[int], k: int) -> int:
        n = len(c)

        c = c + c
        bad = 0

        for i in range(k-1):
            bad += c[i] == c[i+1]

        ans = 0

        for i in range(n):
            ans += bad == 0

            if i != n-1:
                bad += c[i+k-1] == c[i+k]
                bad -= c[i] == c[i+1]

        return ans
