class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n= c.size(), ans=0, x=1;
        vector<int>v(n+k-1);
        for(int i=0; i<n; i++) v[i]= c[i];
        for(int i=0; i<k-1; i++) v[n+i]= c[i];
        for(int i=1; i<n+k-1; i++){
            if(v[i]!=v[i-1]) x++;
            else x=1;
            if(x>=k) ans++;
        }
        return ans;
    }
};
