class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int num = 0;
        int x = 0;
        int y = 1;
        int n = colors.length;

        while (y < n + k && x < n) {
            int tempY = y;
            int temp = tempY % n;

            while (colors[temp] != colors[(temp - 1 + n) % n] && tempY < n + k - 1) {
                tempY++;
                temp = tempY % n;
            }

            num += Math.max(tempY - x - k + 1, 0);
            x = tempY;
            y = x + 1;
        }

        return num;
    }
}
