class Solution {
    public int numberOfAlternatingGroups(int[] arr, int k) {

        int n=arr.length,high=n+k-1;
        int sum=0;
        int cnt=0;
        for(int i=0;i<high;i++)
        {
            if(arr[i%n]==arr[(i-1+n)%n])
                sum=1;
            else
                sum++;

            if(sum>=k)
                cnt++;

    }
        return cnt;
    }
}
