    int numberOfAlternatingGroups(vector<int>& colors, int k) {
    // int n = colors.size();
    // if (k > n) return 0;
    // for (int i = 0; i < k - 1; ++i) {
    //     colors.push_back(colors[i]);
    // }
    // int count = 0;
    // for (int i = 0; i < n; ++i) {
    //     if (isAlternating(colors, i, k)) {
    //         count++;
    //     }
    // }
    // return count;
    int n = colors.size();
    if (k > n) return 0;


    colors.insert(colors.end(), colors.begin(), colors.begin() + k - 1);

    int count = 0;
    int alt = 0;

    for (int i = 0; i < k - 1; ++i) {
        if (colors[i] != colors[i + 1]) {
            alt++;
        }
    }

    if (alt == k - 1) {
        count++;
    }

    for (int i = 1; i < n; ++i) {
        if (colors[i - 1] != colors[i]) {
            alt--;
        }

        if (colors[i + k - 2] != colors[i + k - 1]) {
            alt++;
        }

        if (alt == k - 1) {
            count++;
        }
    }

    return count;
    }
};
