class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();

        int len=1,ans=0;

        for(int i=1;i<n+k-1;i++){
            int curr=colors[i%n];
            int prev=colors[(i-1)%n];

            if(curr!=prev) len++;
            else len=1;

            if(len>=k) ans++;

        }

        return ans;
    }
};
