class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int p = 0;
        int q = 0;
        while (p < colors.size()) {
            if (q == p + k) {
                ans += 1;
                p += 1;
            } else {
                if (colors[q % colors.size()] != colors[(q + colors.size() - 1) % colors.size()]) {
                    q += 1;
                } else {
                    p = q;
                    q = q + 1;
                }
            }
        }
        return ans;
    }
};
