class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        for(int i=0;i<k-1;i++) colors.push_back(colors[i]);
        n = colors.size();
        int ans = 0;
        //int i = 0, j= 0;
        int prev = -1, cnt = 0;
        vector<int> arr(n+1,0);
        for(int i=0;i<n;i++){
            if(prev==-1){
                prev = colors[i];
                cnt = 1;
                arr[i] = cnt;
            }
            else if(prev!=colors[i]){
                cnt++;
                prev = colors[i];
                arr[i] = cnt;
            }
            else{
                prev = colors[i];
                cnt = 1;
                arr[i] = cnt;
            }
        }

        for(int i=0;i<n;i++){
            if(arr[i]>=k) ans++;
        }

        return ans;
    }
};
