class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
         int numGroups = 1;
        int alt = 1;
        int lastNonAltIndex = -1;
        int len = colors.size();

        for (int i = 0; i < k - 1; ++i) {
            if (colors[i] == colors[i + 1]) {
                alt = 0;
                numGroups = 0;
                lastNonAltIndex = i;
                continue;
            }
        }


        for (int j = 1; j < len; ++j) {
            if (alt && colors[(k - 2 + j) % len] != colors[(k + j - 1) % len]) {
                numGroups++;
            } else if (alt && colors[(k - 2 + j) % len] == colors[(k + j - 1) % len]) {
                alt = 0;
                lastNonAltIndex = k + j - 2;
            } else if (!alt && j <= lastNonAltIndex) {
                if (colors[(k - 2 + j) % len] == colors[(k + j - 1) % len]) {
                    lastNonAltIndex = k + j - 2;
                }
                continue;
            } else if (!alt && j > lastNonAltIndex) {
                if (colors[(k - 2 + j) % len] != colors[(k + j - 1) % len]) {
                    numGroups++;
                    alt = 1;
                } else {
                    lastNonAltIndex = k - 2 + j;
                    alt = 0;
                }
            }
        }
        return numGroups;
    }
};
