class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {

        int i = 1 ;
        int j = 0 ;
        int ans = 0 ;
        int n = a.size() ;
        while(j < a.size() && i < n + k - 1)
        {
            if(a[i % n] != a[(i - 1) % n])
            {
                if(i - j + 1 >= k)
                {
                    ans++ ;
                }
                i++ ;
            }
            else
            {
               j = i ;
               i++ ;
            }
        }
        return ans ;
    }
};
