class Solution {
public:
  int numberOfAlternatingGroups(vector<int> &col, int k) {
    int n = col.size(), bad = 0;

    int lst = -1;
    auto res = [&](int i) { return ((i + n) % n); };
    for (int j = n - k + 1; j < n; j++) {
      if (col[res(j)] == col[res(j - 1)]) {
        bad++;
        lst = res(j);
      }
    }
    int ans = (bad == 0);
    int j = n - k + 1;
    for (int i = 0; i < n - 1; i++) {
      if (col[res(i)] == col[res(i - 1)])
        bad++;
      if (col[res(j)] == col[res(j - 1)])
        bad--;
      j++;
      ans += (bad == 0);
    }
    return ans;
  }
};
