    int numberOfAlternatingGroups(vector<int>& vec, int value) {
        vec.insert(vec.end(), vec.begin(), vec.begin() + value);
        int sz = vec.size(), ans = 0;
        vl arr(sz + 1, 0);
        flI(i, 1, sz) { arr[i] += arr[i - 1] + abs(vec[i] - vec[i - 1]); }
        value--;
        int it = value + 1;
        for (; it < sz; it++) {
            int f1=(arr[it] - arr[it - value]);
            if ( f1== value)
                ans++;
        }
        return ans;
    }
