class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        n = len(colors)

        colors = colors + colors

        @cache
        def dp(i):
            if i > 0 and dp(i - 1):
                return colors[i + k - 1] != colors[i + k - 2]

            for j in range(i, i + k - 1):
                if colors[j] == colors[j + 1]:
                    return False

            return True

        res = 0

        for i in range(n):
            if dp(i):
                res += 1

        return res
