class Solution {
public:


int numberOfAlternatingGroups(vector<int>& C, int k) {
    vector<int> extendedC;
    extendedC.reserve(C.size() + k - 1);


    extendedC.insert(extendedC.end(), C.begin(), C.end());
    extendedC.insert(extendedC.end(), C.begin(), C.begin() + k - 1);

    int R = 0, N = 1;

    for (int i = 1; i < extendedC.size(); ++i) {
        if (extendedC[i] != extendedC[i - 1]) {
            ++N;
        } else {
            N = 1;
        }

        if (N >= k) {
            ++R;
        }
    }

    return R;

}

};
