class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = (int)colors.size();
        vector<bool>is(n);
        for (int i = 0;i<n;i++)
            if (colors[i] != colors[(i + 1)%n])is[i] = true;
        set<int>wrong;
        for (int i = 0;i<k - 1;i++)if (!is[i])wrong.insert(i);
        int ans = 0;
        if (wrong.empty())ans = 1;
        for (int i = 1;i<n;i++){
            if (!is[i - 1])wrong.erase(i - 1);
            if (!is[(i + k - 2)%n])wrong.insert((i + k - 2)%n);
            if (wrong.empty())ans++;
        }
        return ans;
    }
};
