class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), pre = 1, ans = 0;
        for (int i = 1; i <= n + k - 2; ++i) {
            pre = colors[i % n] != colors[(i - 1) % n] ? pre + 1 : 1;
            if (pre >= k) {
                ++ans;
            }
        }

        return ans;   
    }
};
