class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(), colors.begin(), colors.begin() + k);
        int n = colors.size(), cnt = 0;
        vector<int> pre(n + 1, 0);

        for (int i = 1; i < n; ++i) {
            pre[i] += pre[i - 1] + abs(colors[i] - colors[i - 1]);
        }
        k--;
        for (int i = k+1; i < n; ++i) {
            if (pre[i] - pre[i - k] == k) {
                 cnt++;
            }
        }

        return cnt;
    }
};
