class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0;i<n;i++)colors.push_back(colors[i]);
        vector<int> s = {0,0};
        for(int i = 1;i<colors.size();i++){
            s.push_back(s.back()+(colors[i]==colors[i-1]));
        }  
        // for(auto e: s)cout << e << " ";
        int cnt = 0;
        for(int i = k;i<k+n;i++){
            if(s[i]-s[i-k+1]==0)cnt++;
        }
        return cnt;
    }
};
