#define sz(x) (int)x.size()

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n {sz(colors)}, ans {}, r {1};
        int dp[n];
        memset(dp,0,sizeof(dp));
        dp[0]=1;
        for (int i = 1; i < n; ++i) {
            dp[i]=1;
            if (colors[i]+colors[i-1]==1) dp[i]+=dp[i-1];
            if (dp[i]==i+1) r=i+1;
            if (dp[i]>=k) ++ans;
        }
        if (colors[n-1]!=colors[0]&&r+dp[n-1]>=k) ans+=min(r+dp[n-1]-k+1,min(k-1,min(r,dp[n-1])));
        return ans;
    }
};
