class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& cs, int k) {
     int ans=0, n=cs.size(), s=0;
        int pr=n-1;
        for(int i=0;i<n+k-1;i++){
            int j=i;
            if(i>=n) j-=n;
            if(cs[j]!=cs[pr]||i==0) {
                s++; 
            if(s==k) ans++, s=k-1;
            }
            else s=1;
            pr=j;
        }
        return ans;
    }
};
