class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k)
    {
        int n=colors.size();
        int s=0;
        for(int i=n-k;i<n;i++)
        {
            if(i>n-k && colors[i]^colors[i-1])
                s++;
        }
        int cnt=0;
        for(int i=0;i<n;i++)
        {
            int j=((i-k+1)%n+n)%n;
            if(colors[j]^colors[((j-1)%n+n)%n])
                s--;
            if(colors[i]^colors[((i-1)%n+n)%n])
                s++;
            if(s==k-1)
                cnt++;
        }
        return cnt;
    }
};
