class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<int> temp = c;
        int n = c.size();
        for(int i = 0;i<k-1;i++) {
            temp.push_back(c[i]);
        }
        vector<int> a(n+k-1, 0);
        for(int i =1;i<n+k-1;i++) {
            if(temp[i] != temp[i-1])
                a[i] = 1;
        }
        int ans = 0;
        int count = 0;
        for(int i =0;i<n+k-1;i++) {
            count+=a[i];
            if(i>=k-1)
                count-=a[i-k+1];
            if(count>=k-1)
                ans++;
            // cout<<a[i]<<"\t"<<count<<"\t"<<ans<<endl;
        }
        return ans;
    }
};
