class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        newcolors = colors+ colors
        # print(newcolors)
        n = len(newcolors)
        i=0
        j=0
        ans = 0

        for j in range(n//2+ k -1):
            if j> 0 and newcolors[j]== newcolors[j-1]:
                i=j

            if i< n//2 and j-i+1>= k:
                ans+=1

        return ans
