class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0; i <= k-2; ++i) colors.push_back(colors[i]);
        vector<int> ok;
        for(int i = 1; i < colors.size()-1; ++i) {
            if (colors[i-1] == colors[i+1] && colors[i] != colors[i-1]) ok.push_back(1);
            else ok.push_back(0);
        }
        // for(int i: ok) cout << i << ' ';
        // cout << endl;
        int cnt = 0, ans = 0;
        for(int i = 0; i < k-2; ++i) cnt += ok[i];
        ans += (cnt == k-2);
        int i = k-2;
        while (i < ok.size()){
            // cout << cnt << ' ';
            cnt += ok[i] - ok[i-(k-2)];
            // cout << cnt << endl;
            ans += (cnt == k-2);
            ++i;
        }
        return ans;
    }
};
