class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<k-1;i++) colors.push_back(colors[i]);
        n+=k-1;
        int ans=0,cnt=0;
        vector<int> &a=colors;
        for(int i=1;i<n;i++){
            cnt+=(a[i]!=a[i-1]);
            if(i>=k-1){
                if(cnt==k-1) ans++;
                cnt-=(a[i-k+1]!=a[i-k+2]);
            }
        }
        return ans;
    }
};
