class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
       int n=colors.size();
       int cnt=0;
       int al=1;
       int i=1;
       while(i<n+k-1){
        int curr=i;
        int pre=i-1;
        if(curr >=n){
            curr -=n;
        }
        if(pre >=n){
            pre -=n;
        }

        if(colors[curr] != colors[pre]){
            ++al;

        }
        else{
            al = 1;
        }
        if(al >=k){
            ++cnt;
        }
        ++i;
       }
       return cnt; 
    }
};
