class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
       int n=colors.size();
       int cnt=1, ans=0;
       for(int i=1;i<n;i++) {
            if(colors[i-1]!=colors[i]) {
                cnt++;
            } else {
                if(cnt>=k) {
                    ans+=(cnt-k)+1;
                }
                cnt=1;
            }

            cout<<i<<" "<<cnt<<endl;
       } 
       if(cnt==n && colors[0]!=colors[n-1]) {
        return n;
       }
       int i=0;
       if(colors[0]!=colors[n-1]) {
            cnt++;

            while(colors[i]!=colors[i+1]) {
                cout<<i<<" "<<cnt<<endl;
                i++;
                cnt++;
            }
        }

        if(cnt>=k) {
            if(i>=(k-1)) {
                cnt-=((i+1)-k)+1;
            }
            ans+=(cnt-k)+1;
        }

        return ans;
    }
};
