class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int originalLength = colors.length;
        int newLength = originalLength + k - 1;
        int[] newColors = new int[newLength];
        System.arraycopy(colors, 0, newColors, 0, originalLength);
        System.arraycopy(colors, 0, newColors, originalLength, k - 1);
        int alternating = 0;
        int consecutive = 0;
        for (int i = 1; i < newLength; i++) {
            if (newColors[i] != newColors[i - 1]) {
                consecutive++;
            } else {
                consecutive = 0;
            }
            if (consecutive >= k - 1) {
                alternating++;
            }
        }
        return alternating;
    }
}
