class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n = nums.size();
        int odd = 0;
        int even = 0;
        int j = 0;
        int i = 0;
        int turn = 0;
        int ans = 0;
        while(i<nums.size()){
            if(i == j){
                j++;
            }
            else{
                if(nums[j%n]%2 != nums[(j-1+n)%n]%2){
                    if((j-i+1) == k){
                        ans++;
                        i++;
                        j++;
                    }else{
                        j++;
                    }
                }
                else{
                    i = j;
                }

            }
        }
        return ans;
    }
};
