class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n=colors.size();
        int ans=0,j=1;
        for(int i=0;i<n;i++)
        {
            bool valid=1;
            if(j<=i)
            {
                j=i+1;
            }
            while(j<(i+k))
            {
                if(colors[(j%n)]==colors[(j-1+n)%n])
                {
                    valid=0;
                    break;
                }
                j++;
            }

            // cout<<i<<" "<<j<<" "<<valid<<endl;

            if(valid==1)
            {
                ans++;
            }
        }

        return ans;
    }
};
