class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int num_found = 0;
        int i = 0;
        int j = 1;
        int n = colors.length;

        while (j < n + k && i < n) {
            int temp_j = j;
            int temp_end = temp_j % n;

            while (colors[temp_end] != colors[(temp_end + n - 1) % n] && (temp_j < (n + k - 1))) {
                temp_j++;
                temp_end = temp_j % n;
            }

            num_found += Math.max(temp_j - i - k + 1, 0);
            i = temp_j;
            j = i + 1;
        }

        return num_found;
    }
}
