class Solution {
public:
     int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int ans = 0;
        int n = arr.size();
        vector<int> v;
        for(int i = 0;i<n;i++){
            if(arr[i] == arr[(i+1)%n])
                v.push_back(i);
        }

        int m=v.size();
         int i = 0;
            while (i<m) {
            if (i!= m - 1) {
                ans += max(0, v[i + 1] - v[i] - k + 1);
            }
            else {
              ans += max(0, v[0] + n-1 -v[i] - k + 2);
            }
            i++;
         }


         if(m==0){
            return n;
         }
         else {
             return ans;
       }
    }
};
