class Solution{
public:
    int numberOfAlternatingGroups(vector<int> &arr, int k) {
        arr.insert(arr.end(), arr.begin(), arr.begin() + k);
        int n = arr.size(), cnt = 0;
        vector<int> pref(n + 1, 0);
        for (int i = 1; i < n; ++i)
            pref[i] += pref[i - 1] + abs(arr[i] - arr[i - 1]);

        k--;
        for (int i = k + 1; i < n; ++i)
        {
            if (pref[i] - pref[i - k] == k)
                cnt++;
        }
        return cnt;
    }
};
