class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0;
        int count=0;
        int n=colors.size();
        int last=colors[0];
        count=1;
        // vector<int> x;
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }
        for(int i=1;i<colors.size();i++){
            if(colors[i]!=last){
                count++;
                last=colors[i];
                if(count>=k){
                    ans++;
                }
            }
            else{
                count=1;
                last=colors[i];
            }
        }
        return ans;

    }
};
