class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:

        res = 0

        l = 0
        sub = colors[l:l+k]

        while l < len(colors):
            new_l = l
            for i in range(k):
                if colors[l%len(colors)] == colors[(l+1)%len(colors)]:
                    new_l = l+1
                    break
                if colors[(l+i)%len(colors)] != colors[(l+i%2)%len(colors)]:
                    new_l = l+i
                    break
            if new_l>l:
                l = new_l
            else:
                res+=1
                l = new_l+1
                while l < len(colors):
                    if colors[(l+k-1)%len(colors)] == colors[(l+k-3)%len(colors)]:
                        res+=1
                        l+=1
                    else:
                        l = l+k-1
                        break






        return res
