class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        int ans =0;
        int j=1;
        int c=1;
        while(j<k+n-1)
        {
            if(a[j%n]!=a[(j-1+n)%n]){
                c++;
            }
            else{
                c=1;
            }
            if(c>=k){
                ans++;
            }
            j++;
        }
        return ans;
    }
};
