func numberOfAlternatingGroups(ar []int, k int) int {
     n := len(ar)
    if n < k {
        return 0
    }


    ar = append(ar, ar...)

    res := 0
    for i:=0; i<n; i++ {
        j := i
        for j+1<len(ar) && ar[j] != ar[j+1] {
            j++
        }

        l := j-i+1
        if l >= k {
            res += l-k+1
            if j >= n {
                l2 := j-n+1
                if l2 >= k {
                    rep := l2-k+1
                    res -= rep
                }
            }
        }

        i = j
    }
    return res
}
