class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int ans = 0;
        vector<bool> isAltered(n,false);
        for(int i=0;i<n;i++){
            int r = (i+1)%n;
            if(colors[i] != colors[r]) isAltered[i] = true;
        }
        int cnt_error = 0;
        for(int i=0;i<k-1;i++){
            if(!isAltered[i]) cnt_error++;
        }
        if(!cnt_error) ans++;

        for(int i=0;i<n-1;i++){

            if(!isAltered[i]) cnt_error--;
            int r = (i+k-1)%n;
            if(!isAltered[r]) cnt_error++;

            if(!cnt_error) ans++;
            ///cout<<ans<<endl;
        }
        return ans;
    }
};
