class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n =c.size();
        int cnt = 0; 
        if(k==1){ 
            return n; 
        } 
        int len = 1; 
        int i = 1; 
        while(i<n+k-1){ 
            int curr =i; 
            int prev=i-1; 
            if (curr >=n) curr -=n;
            if (prev>=n) prev-=n;
            if(c[curr] != c[prev]) ++len;
            else len = 1; 
            if(len>=k) ++cnt;
            ++i; 
        } 
        return cnt; 

    }
};
