class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0; i<k-1; i++){
            colors.push_back(colors[i]);
        }
        vector<int>v;
        int cnt = 1;
        for(int i=0; i<colors.size()-1; i++){
            if(colors[i]!=colors[i+1]){
                cnt++;
            }
            else{
                v.push_back(cnt);
                cnt = 1;
            }
        }
        v.push_back(cnt);
        int ans = 0;
        for(int i=0; i<v.size(); i++){
            cout<<v[i]<<" ";
            if(v[i]>=k){
                ans += v[i]-k+1;
            }
        }
        return ans;

    }
};
