#include "bits/stdc++.h"
using namespace std;
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int K) {
    	vector<int>A;
    	int N=a.size();
    	for(int i=0;i<N*2;i++)
    	{
    		A.push_back(a[i%N]);
    	}
    	// int N=A.size();
    	set<int>s;
    	for(int i=0;i<2*N-1;i++)
    	{
    		if(A[i]==A[i+1])
    			s.insert(i);
    	}
    	int ans=0;
    	for(int i=0;i<N;i++)
    	{
    		int end=i+K-2;
    		auto it=s.lower_bound(i);
    		if(it==s.end() || (*it)>end)
    			ans++;
    	}
    	return ans;

    }
};
