class Solution(object):
    def numberOfAlternatingGroups(self, colors, k):

        ans = 0
        n = len(colors)

        if k == 1:
            return n

        al = 1
        for i in range(1,n+k-1):
            a = i%n
            b = (i-1)%n
            if colors[a] != colors[b] : 
                al +=1
            else:
                al = 1

            if al >= k:
                ans +=1

        return ans
