class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        if(k==1)
        {
            return n;

        }
        long long ans=0;
        int count=1;
        int i=1;
        while(i<n+k-1)
        {
            int curr=i;
            if(curr>=n)
            {
                curr=curr-n;
            }
            int prev=i-1;
            if(prev>=n)
            {
                prev=prev-n;
            }
            if(colors[curr]!=colors[prev])
            {
                count++;
            }
            else{
                count=1;
            }
            if(count>=k)
            {
                ans++;
            }


            i++;

        }
        return ans;
    }
};
